// Obter a string de nome completo do nó anterior
const nomeCompleto = item.json.nomeCompleto;

// Função para separar o nome completo em nome e sobrenome
function separarNomeSobrenome(nomeCompleto) {
  // Verificar se o nome completo é uma string
  if (typeof nomeCompleto !== 'string') {
    throw new Error('O nome completo deve ser uma string.');
  }

  // Dividir o nome completo em palavras
  const palavras = nomeCompleto.trim().split(' ');

  // Verificar se há mais de uma palavra
  if (palavras.length === 0) {
    throw new Error('O nome completo deve conter pelo menos um nome.');
  }

  // Extrair o primeiro nome
  const primeiroNome = palavras.shift();

  // Extrair o sobrenome
  const sobrenome = palavras.length > 0 ? palavras.join(' ') : '.';

  // Formatar o nome e sobrenome com a primeira letra de cada palavra em maiúscula
  const nomeFormatado = formatarNome(primeiroNome);
  const sobrenomeFormatado = formatarNome(sobrenome);

  return {
    nome: nomeFormatado,
    sobrenome: sobrenomeFormatado
  };
}

function formatarNome(nome) {
  return nome
    .toLowerCase()
    .split(' ')
    .map((palavra) => palavra.charAt(0).toUpperCase() + palavra.slice(1))
    .join(' ');
}

